package arrays;

public class Arrays2ds {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int weeks=4;
		int days=7;
		for(int i=1;i<=weeks;i++) {
		   System.out.println("Week "+i);
		   for(int j=1;j<=days;j++) {
			   System.out.println("Days"+j);
		   }
		   
		}
	}

}
